import React, { useContext, useEffect, useState } from 'react';
import { GlobalState } from '../../.././GlobalState';
import axios from 'axios';

function Content() {
    const state = useContext(GlobalState);
    const [cart] = state.usersAPI.cart;
    const [info, setInfo] = useState([]);

    console.log("Cart: ");
    console.log(cart);

    // const [total, setTotal] = useState(0);


    const [order, setOrder] = useState({
        user: '', name: '', phone: '', address: '', email: '', note: '', info: []
    });

    // const [order, setOrder] = useState({});

    

    let productsBuy = [];
    // productsBuy = [...productsBuy, {id: "Lam", pwd: "Hoang"}];
    useEffect(() => {
        cart.map(item => productsBuy = [...productsBuy, {_id: item._id, product_id: item.product_id, title: item.title, price: item.price, sale: item.sale, image: item.image, category: item.category, quantity: item.quantity}]);
        setInfo(productsBuy);
        console.log("Info:");
    
        console.log(info);
    }, [cart]);
    // console.log(info);
    // setInfo([...info, {_id: item._id, product_id: item.product_id, title: item.title, price: item.price, sale: item.sale, image: item.image, category: item.category, quantity: item.quantity}])
    // setOrder({ info: productsBuy })



    const onChangeInput = e => {
        // const { name, value } = e.target;
        // setOrder({ ...order, [name]: value });
    }

    const orderSubmit = async e => {
        // e.preventDefault();
        // try {
        //     await axios.post('/api/checkout', { ...order });

        //     alert('Order has been submitted');
        // } catch (err) {
        //     alert(err.response.data.msg);
        // }
    }

    // useEffect(() => {
    //     const getTotal = () => {
    //         const total = cart.reduce((prev, item) => {
    //             return prev + (item.price * item.quantity);
    //         }, 0);

    //         setTotal(total);
    //     }

    //     getTotal();
    // }, [cart]);

    const checkoutBtn = () => {
        if (window.confirm("Are you sure you want to checkout?")) {
            orderSubmit();
        }
    }

    return (
        <div className="mock">
            <div className="mock_row1">
                <form>
                    <div className="mock__input">
                        <label htmlFor="field__input">Name: <span>*</span></label>
                        <input
                            id="field__input"
                            type="text"
                            value={order.name}
                            onChange={onChangeInput}
                            required />
                    </div>
                    <div className="mock__input">
                        <label htmlFor="field__input">Address: <span>*</span></label>
                        <input
                            id="field__input"
                            type="text"
                            value={order.address}
                            onChange={onChangeInput}
                            required />
                    </div>
                    <div className="mock__input">
                        <label htmlFor="field__input">Phone: <span>*</span></label>
                        <input
                            id="field__input"
                            type="text"
                            value={order.phone}
                            onChange={onChangeInput}
                            required />
                    </div>
                    <div className="mock__input">
                        <label htmlFor="field__input">Email: <span>*</span></label>
                        <input
                            id="field__input"
                            type="email"
                            value={order.email}
                            onChange={onChangeInput}
                            required />
                    </div>
                    <div className="mock__input">
                        <label htmlFor="field__input">Note: </label>
                        <input
                            id="field__input"
                            type="text"
                            value={order.note}
                            onChange={onChangeInput} />
                    </div>
                    <button onClick={() => checkoutBtn()} className="add-1">Check out</button>
                </form>
                {/* <a className=" add-1" href="single.html">Check out</a> */}
            </div>
            <div className="mock_row2">
                <table className="table">
                    <tbody>
                        <tr>
                            <th className="t-head">Product</th>
                            <th className="t-head">Total</th>
                        </tr>
                        {
                            // cart.map(product => (
                            //     <tr className="cross">
                            //         <td className="t-data">{product.product_id} ( x<span>{product.quantity}</span> )</td>
                            //         <td className="t-data">{product.price * product.quantity}</td>
                            //     </tr>
                            // ))
                        }
                        {/* <tr className="cross">
                            <td className="t-data">Product ( x<span>5</span> )</td>
                            <td className="t-data">150000</td>
                        </tr> */}
                        <tr>
                            {/* <td className="table__total" colSpan={2}>Total: <span>{total}</span></td> */}
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Content;
