import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Content() {
    const [user, setUser] = useState({
        email: '',
        password: ''
    });

    const onChangeInput = e =>{
        const{name, value} = e.target;
        setUser({...user, [name]:value});
    }

    
    return (
        <div className="login">
            <div className="main-agileits">
                <div className="form-w3agile">
                    <h3>Login</h3>
                    <form action="#" method="post">
                        <div className="key">
                            <i className="fa fa-envelope" aria-hidden="true" />
                            <input type="text" defaultValue="Email" name="Email" onfocus="this.value = '';" onblur="if (this.value == '') {this.value = 'Email';}" onChange={onChangeInput} required />
                            <div className="clearfix" />
                        </div>
                        <div className="key">
                            <i className="fa fa-lock" aria-hidden="true" />
                            <input type="password" defaultValue="Password" name="Password" onfocus="this.value = '';" onblur="if (this.value == '') {this.value = 'Password';}" onChange={onChangeInput} required />
                            <div className="clearfix" />
                        </div>
                        <input type="submit" defaultValue="Login" />
                    </form>
                </div>
                <div className="forg">
                    <a href="/" className="forg-left">Forgot Password</a>
                    <Link to="/register" className="forg-right">Register</Link>
                    <div className="clearfix" />
                </div>
            </div>
        </div>
    );
}

export default Content;
